  const [componentProps, elementProps] = separateComponentProps(
    otherProps,
    componentKeys,
  );
  const [css, classes] = getCssClasses<Breakpoint>(componentProps);

  return (
    <Component
      {...elementProps}
      ref={ref}
      className={[props.className, "slang-box", ...classes]
        .filter(Boolean)
        .join(" ")
        .trim()}
      style={{ ...css, ...props.style }}
    />
  );
